# Part 2
floor = 0
position = nil

INPUT.chars.each_with_index do |c, i|
  floor += (c == '(' ? 1 : -1)
  if floor == -1
    position = i + 1
    break
  end
end

puts "Part 2: First basement position = #{position}"
